def word2num (textnum, numwords={}):
    if not numwords:
        satuan = ['', 'satu', 'dua', 'tiga', 'empat', 'lima',
          'enam', 'tujuh', 'delapan', 'sembilan', 'sepuluh', 'sebelas', 'duabelas', 'tigabelas', 
          'empatbelas', 'limabelas ', 'enambelas', 'tujuhbelas', 'delapanbelas', 'sembilanbelas']

        puluhan = ["", "", "duapuluh", "tigapuluh", "empatpuluh", "limapuluh", "enampuluh", 
                   "tujuhpuluh", "delapanpuluh", "sembilanpuluh"]

        ukuran = ["ratus", "ribu", "juta", "miliar", "triliyun"]

        numwords["and"] = (1, 0)
        for idx, word in enumerate(satuan): numwords[word] = (1, idx)
        for idx, word in enumerate(puluhan): numwords[word] = (1, idx * 10)
        for idx, word in enumerate(ukuran): numwords[word] = (10 ** (idx * 3 or 2), 0)

    ordinal_words = {'pertama':1, 'kedua':2, 'ketiga':3, 'keempat':4, 'kelima':5, 'keenam':6,
                     'ketujuh':7, 'kedelapan':8, 'kesembilan':9, 'kesepuluh':10}

    textnum = textnum.replace('-',' ')

    current = result = 0
    curstring = ""
    onnumber = False
    for word in textnum.split():
        if word in ordinal_words:
            scale, increment = (1, ordinal_words[word])
            current = current * scale + increment
            if scale > 100:
                result += current
                current = 0
            onnumber = True
        else:

            if word not in numwords:
                if onnumber:
                    curstring += repr(result + current) + " "
                curstring += word + " "
                result = current = 0
                onnumber = False
            else:
                scale, increment = numwords[word]

                current = current * scale + increment
                if scale > 100:
                    result += current
                    current = 0
                onnumber = True

    if onnumber:
        curstring += repr(result + current)

    return curstring

