		return tens[(int)(num/10)-2] + ('' if num%10==0 else ' ' + under_20[num%10])

	pivot = max([key for key in above_100.keys() if key <= num])

	return num2words((int)(num/pivot)) + ' ' + above_100[pivot] + ('' if num%pivot==0 else ' ' + num2words(num%pivot))

