                elif (int(a[j])!=0) :
                    word = word + satuan[int(a[j])] + puluhan[len(a)-j]
                elif (a == "000"):
                    triple0=True
                    break
            else:
                word = word + satuan[int(a[j])] + puluhan[len(a)-j]

        if (i!=len(num)-1) and triple0==False:
            word = word + puluhan[len(num)+2-i]         

    return word+word_koma+word_last

def word2num(word):
    word = word_tokenize(word) 
    num=""
    for i in range (len(word)):
        a=word[i]+" "
        if a in satuan:
            if num[-2:-1] == "0" and (i+1==len(word)):
                    num = str(num[:-1]) + str(int(satuan.index(a)))
            else:
                num = str(num) + str(int(satuan.index(a)))
        else :

            if(a[:2]=="se"):
                a=a[2:]
                num = str(num) + "1"
            if(a=="belas "): 
                num = str(num[:-3]) + "1" + str(num[-1])
            elif(a=="puluh ") and (i+1==len(word)) :
                num = str(num[:-3]) + str(int(num[-1]))  + "0" 
            elif(a=="puluh "): 
                if(num[-4:-1]) == "000":
                    num = str(num[:-4]) + str(int(num[-1])) 
                elif(num[-3:-1]) == "00":
                    num = str(num[:-3]) + str(int(num[-1])) 
                elif(num[-2:-1]) == "0":
                    num = str(num[:-2]) + str(int(num[-1]))
                if word[i+1]=="ribu" or word[i+1]=="juta":
                    num = str(num) + "0"
            elif(a=="ratus "):
                if num[-8:-1] == "000.000":
                    num = str(num[:-4]) + str(int(num[-1])) +"00"
                elif num[-4:-1] == "000" :
                    num = str(num[:-4]) + str(int(num[-1])) +"00"
                else:
                    num = str(num) + "00"
            elif(a=="ribu "):
                if(num[-3:-1]) == "00":
                    num = str(num[:-2]) + str(int(num[-1]))
                if num[-8:-4] == ".000": #ratus
                    num = str(num[:-7]) + str(int(num[-3:])) + ".000"
                elif num[-7:-3] == ".000": #puluh
                    num = str(num[:-6]) + "0" + str(int(num[-2:])) + ".000"
                elif num[-9:-5] == ".000": #satu
                    num = str(num[:-8]) + "00" + str(int(num[-4:])) + ".000"
                else:
                    num = str(num) + ".000"
            elif(a=="juta "): 
                num = str(num) + ".000.000"
            elif(a=="persen "): 
                num = str(num) + "%"
            elif(a=="rupiah "): 
                num = "Rp. " + str(num) 
            elif(a=="koma "): 
                num = str(num) + ","

    return num

