    for a in angka:
        if(re.findall("\d",a)):
            temp+=a
        elif(a==','):
            hasil.append(int(temp))
            temp=''
            hasil.append('Koma')
        else:
            hasil.append(int(temp))
            temp=''
        if(indeks == len(angka)-1):
            hasil.append(int(temp))
