    satuan = ['' , 'Satu ', 'Dua ', 'Tiga ','Empat ', 'Lima ', 'Enam ', 'Tujuh ', 'Delapan ', 'Sembilan ']
    angka= hpstitik(angka)
    koma = pisah(angka)
    kalimat = ''
    titik=len(angka)
    for a in angka:
        if(int(a/100) == 1):
            kalimat +='Seratus '
            a= a%100
        elif(int(a/100) > 1 ):
            kalimat += satuan[int(a/100)]+'Ratus '
            a= a%100

        if(int(a/10)>1):
            kalimat += satuan[int(a/10)]+'Puluh '
            a= (a%10)
        elif(int(a/10)==1):
            a%=10
            if(a == 0):
                kalimat += 'Sepuluh '
                a-=a
            elif(a == 1):
                kalimat += 'Sebelas '
                a-=a
            else:
                kalimat += satuan[int(a)]+'Belas '
                a-=a
        kalimat += satuan[int(a)]
        kalimat += besaran[titik]
        titik-=1

    for a in koma:
        if(a=='Koma'):
            kalimat+= a+' '
        else:
            for b in str(a):
                kalimat += satuan[int(b)]

    return kalimat

