def text2int (textnum, words={}):
    if not words:
        units = [
        "kosong", "satu", "dua", "tiga", "empat", "lima", "enam", "tujuh", "delapan",
        "sembilan", "sepuluh", "sebelas", "duabelas", "tigabelas", "empatbelas", "limabelas",
        "enambelas", "tujuhbelas", "delapanbelas", "sembilanbelas",
        ]

        tens = ["", "", "duapuluh", "tigapuluh", "empatpuluh", "limapuluh", "enampuluh", "tujuhpuluh", "delapanpuluh", "sembilanpuluh"]

        scales = ["ratus", "ribu", "juta", "milyar", "triliun"]

        words["and"] = (1, 0)
        for idx, word in enumerate(units):  words[word] = (1, idx)
        for idx, word in enumerate(tens):   words[word] = (1, idx * 10)
        for idx, word in enumerate(scales): words[word] = (10 ** (idx * 3 or 2), 0)

    textnum = textnum.replace('-', ' ')

    current = result = 0
    curstring = ""
    onnumber = False
    for word in textnum.split():
        if word not in words:
             if onnumber:
                curstring += repr(result + current) + " "
             curstring += word + " "
             result = current = 0
             onnumber = False
        else:
             scale, increment = words[word]

             current = current * scale + increment
             if scale > 100:
                 result += current
                 current = 0
             onnumber = True

    if onnumber:
        curstring += repr(result + current)

    return curstring


